import 'package:flutter/material.dart';import 'package:habit_tracker/screens/home_screen.dart';import 'package:habit_tracker/screens/register_screen.dart';import 'package:habit_tracker/services/auth_provider.dart';import 'package:habit_tracker/widgets/custom_button.dart';import 'package:provider/provider.dart';class WelcomeScreen extends StatefulWidget {  const WelcomeScreen({Key? key}) : super(key: key);  @override  State<WelcomeScreen> createState() => _WelcomeScreenState();}class _WelcomeScreenState extends State<WelcomeScreen> {  @override  Widget build(BuildContext context) {    final auth_prov = Provider.of<AuthProvider>(context, listen: false);    return Scaffold(      body: SafeArea(          child: Center(        child: Padding(            padding: EdgeInsets.symmetric(vertical: 25, horizontal: 35),            child: Column(              crossAxisAlignment: CrossAxisAlignment.center,              mainAxisAlignment: MainAxisAlignment.center,              children: [                Image.asset(                  "asset/image1.png",                  height: 300,                ),                const SizedBox(height: 20),                const Text(                  "Let's Get Started",                  style: TextStyle(                    fontSize: 22,                    fontWeight: FontWeight.bold,                  ),                ),                const SizedBox(height: 10),                const Text(                  "Never a Better Time Than Now To Start.",                  style: TextStyle(                    fontSize: 14,                    color: Colors.black38,                    fontWeight: FontWeight.bold,                  ),                ),                const SizedBox(height: 20),                SizedBox(                  width: double.infinity,                  height: 50,                  child: CustomButton(                    onPressed: () async {                      // When True, Then Fetch The Shared Preference Data                      if (auth_prov.isSignedIn == true) {                        await auth_prov                            .getDataFromSharedPreferences()                            .whenComplete(                              () => Navigator.pushReplacement(                                context,                                MaterialPageRoute(                                  builder: (context) => const HomeScreen(),                                ),                              ),                            );                      } else {                        Navigator.pushReplacement(                          context,                          MaterialPageRoute(                            builder: (context) => const RegisterScreen(),                          ),                        );                      }                    },                    text: "Get Started",                  ),                )              ],            )),      )),    );  }}